SET client_encoding = 'UTF8';

BEGIN;

-- Insert German group template translations
INSERT INTO group_template_translations (template_id, language, title, description)
SELECT t.id, 'de', v.de_title, v.de_description
FROM (VALUES
  ('30-Day Gratitude Journal', 'Tagebuch der 30-Tage-Dankbarkeit', 'Schreibe täglich Einträge der Dankbarkeit.'),
  ('Screen Time', 'Bildschirmzeit', 'Hol dir deine Aufmerksamkeit zurück. Protokolliere deine bildschirmfreie Zeit, teile Strategien und haltet euch gegenseitig zu weniger sinnlosem Scrollen.'),
  ('7-Day Launch Sprint', '7-Tage-Start-Sprint', 'Sieben Tage konsistentes tägliches Protokollieren, um eine neue Gewohnheit zu starten. Hohe Intensität, kurzer Einsatz. Perfekt für alle, die gerade anfangen und beweisen möchten, dass sie sich jeden Tag zeigen können.'),
  ('The 365-Day Marathon', 'Der 365-Tage-Marathon', 'Ein ganzes Jahr einer nicht verhandelbaren Gewohnheit. Kein Sprint — ein Lebensstiländerung. Für Menschen, die 30-Tage-Resets leid sind und etwas Permanentes aufbauen möchten.'),
  ('30 Days of Coding', '30 Tage Programmieren', 'Schreibe jeden Tag Code.'),
  ('Morning Routines', 'Morgenroutinen', 'Gewinne den Morgen zusammen. Egal ob deine Routine Tagebuchführung, Bewegung, kalte Dusche oder Lesen ist — protokolliere sie und bleib konsistent.'),
  ('Wake Up at 5am', 'Um 5 Uhr aufwachen', 'Wache 21 Tage lang um 5 Uhr auf.'),
  ('Home Cooking', 'Kochen zu Hause', 'Koche mehr, iss besser, gib weniger aus. Protokolliere, wenn du zu Hause gekocht hast, und teile, was du gemacht hast. Keine Rezepte nötig — nur die Gewohnheit.'),
  ('8 Glasses of Water', '8 Gläser Wasser', 'Trinke jeden Tag 8 Gläser.'),
  ('Gardening Club', 'Gartenclub', 'Verfolge deinen Garten zusammen — gießen, pflanzen, jäten und ernten. Perfekt für Pflanzenfreunde, die verantwortlich für ihr Grundstück sein möchten.'),
  ('Book a Month', 'Ein Buch pro Monat', 'Lese jeden Tag 30 Minuten.'),
  ('Couch to 5K', 'Vom Sofa zum 5K', 'Baue eine Laufgewohnheit auf, indem du dich jeden Tag zeigst.'),
  ('No Alcohol for 30 Days', '30 Tage ohne Alkohol', 'Vermeide Alkohol für 30 Tage.'),
  ('Daily Discovery', 'Tägliche Entdeckung', 'Lerne jeden Tag etwas Neues — eine Tatsache, eine Fähigkeit, ein Konzept oder ein Handwerk. Teile es mit der Gruppe, damit ihr euch gegenseitig lernen könnt.'),
  ('Digital Detox Weekend', 'Digitale Detox am Wochenende', 'Zwei volle Tage komplett ohne unnötige Bildschirme.'),
  ('30-Day Read to My Kids Challenge', 'Lese meinen Kindern 30 Tage lang vor', 'Lese deinen Kindern jeden Tag 30 Tage lang vor. Selbst 10 Minuten vor dem Schlafengehen bauen ein Leben lang Lesealand auf — und geben dir einen Grund, zusammen zu verlangsamen.'),
  ('Whole30', 'Whole30', 'Iss jeden Tag ausschließlich ganze Lebensmittel.'),
  ('No Social Media for 30 Days', '30 Tage ohne soziale Medien', 'Bleibe jeden Tag weg von sozialen Medien.'),
  ('Cold Shower Challenge', 'Kaltduschen-Herausforderung', 'Nimm jeden Tag eine kalte Dusche.'),
  ('Language Practice', 'Sprachpraxis', 'Tägliches Üben macht fließend. Haltet euch gegenseitig auf der Spur, ob du Spanisch, Japanisch oder etwas anderes lernst.'),
  ('Alcohol-Free', 'Alkoholfrei', 'Egal ob du neugierig auf Alkohol bist, in Genesung bist oder einfach mit Alkohol fertig bist — das ist eine langfristige Verantwortungsgruppe, kein 30-Tage-Reset. Ruhig, unterstützend, kein Druck.'),
  ('Nature Walks', 'Spaziergänge in der Natur', 'Geh raus, atme, protokolliere es. Eine sanfte Verantwortungsgruppe für Menschen, die Natur zu einer regelmäßigen Gewohnheit machen möchten.'),
  ('Weekend Workshop', 'Wochenend-Workshop', 'Mach etwas, repariere etwas oder baue etwas jedes Wochenende. Kleine Erfolge zählen — ein Patch, ein Farbauftrag, eine festgezogene Scharniere.'),
  ('Morning Workout', 'Morgen-Trainingseinheit', 'Trainiere vor 9 Uhr 21 Tage lang.'),
  ('The Weekly Stretch', 'Der wöchentliche Dehnung', 'Persönlicher Mut als Praxis. Mache einmal pro Woche etwas, das dir unbequem ist — ein schwieriges Gespräch, eine neue soziale Situation, ein kreativer Risiko — und protokolliere es. Wachstum liegt außerhalb deiner Komfortzone.'),
  ('10K Steps for a Week', '10.000 Schritte für eine Woche', 'Ein kurzer 7-Tage-Bewegungs-Sprint.'),
  ('Prayer Group', 'Gebetsgruppe', 'Bete konsistent zusammen. Protokolliere deine tägliche stille Zeit und haltet euch gegenseitig verankert.'),
  ('Read the New Testament in 260 Days', 'Lese das Neue Testament in 260 Tagen', 'Lese jeden Tag ein Kapitel'),
  ('Track Every Dollar', 'Verfolge jeden Dollar', 'Protokolliere jede tägliche Ausgabe.'),
  ('Running Club', 'Laufclub', 'Protokolliere deine Läufe, teile deine Strecken und bleib aktiv. Kein Enddatum — nur konsistente Kilometer.'),
  ('The Weekend Warrior', 'Der Wochenend-Krieger', 'Eine Hauswartungsaufgabe pro Woche. Repariere ein Leck, male ein Zimmer, warte das Auto — was auch immer dieses Wochenende. Protokolliere es und halte das Haus zusammen in guter Form.'),
  ('Out There Doing It', 'Raus und mach Sachen', 'Kein spezifisches Projekt, kein spezifisches Ziel. Nur eine Gruppe von Menschen, die sich zeigen und beweisen, dass sie jeden Tag etwas Produktives getan haben. Protokolliere es. Alles zählt.'),
  ('Writers'' Room', 'Schreibzimmer', 'Schreibe konsistent mit Menschen, die den Kampf verstehen. Protokolliere deine Wortanzahl oder nur dass du dich gezeigt hast.'),
  ('Staying Connected', 'Verbunden bleiben', 'Beziehungen brauchen Wartung. Eine Gruppe für Menschen, die regelmäßigen Kontakt mit Freunden und Familie zu einer nicht verhandelbaren Gewohnheit machen möchten — nicht nur eine 30-Tage-Herausforderung.'),
  ('10K Steps Daily', '10.000 Schritte täglich', 'Mache 30 Tage lang jeden Tag 10.000 Schritte.'),
  ('Inbox Zero for a Month', 'Inbox Null für einen Monat', 'Leere deine Inbox jeden Tag.'),
  ('Daily Steps', 'Tägliche Schritte', '10.000 Schritte ist die Herausforderung. Das ist der Lebensstil. Eine permanente Gruppe für Menschen, die ihre Schritte jeden Tag protokollieren und möchten, dass andere dasselbe tun.'),
  ('Book Club', 'Buchclub', 'Lese zusammen, teile deine Gedanken, halte euch gegenseitig im Tempo. Funktioniert für jedes Genre oder jede Lesegeschwindigkeit.'),
  ('100 Pushups a Day', '100 Liegestütze pro Tag', 'Schiebe deine Konsistenz mit 100 täglichen Liegestützen voran.'),
  ('30 Days of Kindness', '30 Tage der Freundlichkeit', 'Tue jeden Tag eine freundliche Tat.'),
  ('Skill Building', 'Fähigkeitsentwicklung', 'Wähle eine Fähigkeit, übe konsistent, protokolliere deine Sitzungen. Egal ob Gitarre, Tischlerei, Schach oder Aquarell — absichtliche Praxis braucht Verantwortung.'),
  ('30-Day No Sugar Challenge', '30-Tage-Herausforderung ohne Zucker', 'Vermeide 30 Tage lang täglich zugesetzten Zucker.'),
  ('Bird Watching', 'Vogelbeobachtung', 'Protokolliere deine Beobachtungen, verfolge deine Lebensliste und teile die Freude an Vögeln mit Menschen, die verstehen.'),
  ('Gratitude Journal', 'Dankbarkeitstagebuch', 'Drei Dinge, jeden Tag. Eine einfache Praxis, die sich im Laufe der Zeit zusammensetzt. Kontinuierliche Verantwortung für diejenigen, die von der Herausforderung zur Gewohnheit übergegangen sind.'),
  ('Creative Practice', 'Kreative Praxis', 'Erstelle etwas — egal was — konsistent. Malen, Fotografie, Musik, Keramik, Design. Das Medium spielt keine Rolle; sich zeigen tut es.'),
  ('DIY & Home Projects', 'DIY & Heimprojekte', 'Behalte den Schwung deiner Heimprojekte. Protokolliere deinen Fortschritt, teile woran du arbeitest und erhalte Verantwortung, um tatsächlich zu beenden.'),
  ('No Spend Challenge', 'Herausforderung ohne Ausgaben', 'Keine unnötigen Käufe jeden Tag.'),
  ('Daily Coding', 'Tägliches Programmieren', '30 Tage Code geben dir die Gewohnheit. Das ist, wo du sie behältst. Eine Gruppe für Entwickler, Lernende und Baumeister, die jeden Tag Code schreiben möchten — auf unbestimmte Zeit.'),
  ('Save $5 a Day', 'Spare 5 € pro Tag', 'Spare jeden Tag 5 €.'),
  ('The Polymath Project', 'Das Polymath-Projekt', 'Eine Gruppe für die wirklich Neugierigen. Lerne jeden Tag etwas Neues — egal was — und protokolliere eine kurze Zusammenfassung davon, was es war und warum es wichtig ist. Das Ziel ist Breite, nicht Tiefe.'),
  ('Sleep Accountability', 'Schlaf-Verantwortung', 'Schlaf ist die Grundlage von allem. Protokolliere deine Stunden, halte euch gegenseitig zu konsistenten Schlafenszeiten und spüre den Unterschied.'),
  ('50 Books in a Year', '50 Bücher in einem Jahr', 'Behalte tägliche Lektüre und wöchentliche Fortschritt der Bücher.'),
  ('Daily Hydration', 'Tägliche Hydration', 'Acht Gläser scheint einfach. Das täglich ohne Verantwortung zu tun ist schwieriger, als es aussieht. Eine entspannte Gruppe, um euch gegenseitig zu halten, um genug Wasser zu trinken.'),
  ('Read the Bible in 400 days', 'Lese die Bibel in 400 Tagen', 'Lese drei Kapitel pro Tag'),
  ('Healthy Eating', 'Gesunde Ernährung', 'Kein Diät — eine langfristige Gewohnheit. Protokolliere, wenn du gut gegessen hast, teile, was funktioniert und halte euch gegenseitig auf der Spur ohne Alles-oder-Nichts-Druck.'),
  ('7 Hours of Sleep', '7 Stunden Schlaf', 'Erreiche jede Nacht mindestens 7 Stunden.'),
  ('Plank Challenge', 'Planken-Herausforderung', 'Tägliche Planken mit wöchentlichem Fortschritt.'),
  ('Family Fitness', 'Familien-Fitness', 'Beweg die ganze Familie. Verfolge Schritte, Trainingseinheiten oder alles, was ihr aktiv zusammen tut.'),
  ('Strength Training', 'Krafttraining', 'Werde zusammen stark. Protokolliere deine Sitzungen, verfolge deine Hebel und halte euch gegenseitig konsistent.'),
  ('Gym Buddies', 'Gym-Partner', 'Zeige dich, protokolliere es, halte euch gegenseitig verantwortlich. Die Gruppe, die zusammen trainiert, bleibt zusammen.'),
  ('Phone Call a Day', 'Ein Anruf pro Tag', 'Mache jeden Tag einen Telefonanruf.'),
  ('Language Learning Sprint', 'Sprach-Lern-Sprint', 'Lerne die Sprache täglich 15 Minuten.'),
  ('Cook at Home', 'Zu Hause kochen', 'Koche jeden Tag zu Hause.'),
  ('Savings Group', 'Spargruppe', 'Spare konsistent und halte euch gegenseitig. Protokolliere deine Beiträge und beobachte, wie die Gewohnheit haftet.'),
  ('Daily Journaling', 'Tägliche Tagebuchführung', 'Schreibe täglich in dein Tagebuch.'),
  ('Daily Scripture', 'Tägliche Schrift', 'Bibel, Koran, Tora oder jeden heiligen Text — ein permanentes Zuhause für diejenigen, die heilige Texte konsistent lesen und sich gegenseitig für die Praxis verantwortlich machen möchten.'),
  ('Reach Out Daily', 'Täglich Kontakt aufnehmen', 'Kontaktiere jeden Tag eine Person.'),
  ('Budget Accountability', 'Budget-Verantwortung', 'Verfolge jeden Dollar zusammen. Protokolliere, wenn du dein Budget überprüft oder deine Ausgaben für den Tag verfolgt hast. Bewusstsein ist der erste Schritt.')
) AS v(en_title, de_title, de_description)
JOIN group_templates t ON t.title = v.en_title
ON CONFLICT (template_id, language) DO NOTHING;

