SET client_encoding = 'UTF8';

BEGIN;

-- Insert French group template translations
INSERT INTO group_template_translations (template_id, language, title, description)
SELECT t.id, 'fr', v.fr_title, v.fr_description
FROM (VALUES
  ('30-Day Gratitude Journal', 'Journal de gratitude de 30 jours', 'Écrivez des entrées de gratitude quotidiennes.'),
  ('Screen Time', 'Temps d''écran', 'Reprenez votre attention. Enregistrez votre temps sans téléphone, partagez des stratégies et tenez-vous mutuellement pour moins de défilement insensé.'),
  ('7-Day Launch Sprint', 'Lancement de 7 jours', 'Sept jours d''enregistrement quotidien constant pour lancer une nouvelle habitude. Intensité élevée, engagement court. Parfait pour quiconque vient de commencer et veut prouver qu''il peut se présenter tous les jours.'),
  ('The 365-Day Marathon', 'Le marathon 365 jours', 'Une année complète d''une seule habitude non négociable. Pas un sprint — un changement de mode de vie. Pour ceux qui en ont fini avec les remises à zéro de 30 jours et qui veulent construire quelque chose de permanent.'),
  ('30 Days of Coding', '30 jours de codage', 'Écrivez du code tous les jours.'),
  ('Morning Routines', 'Routines matinales', 'Gagnez le matin ensemble. Que votre routine soit journalisation, exercice, douche froide ou lecture — enregistrez-la et restez cohérent.'),
  ('Wake Up at 5am', 'Se réveiller à 5h', 'Se réveiller avant 5h pendant 21 jours.'),
  ('Home Cooking', 'Cuisine à la maison', 'Cuisinez plus, mangez mieux, dépensez moins. Enregistrez quand vous avez cuit à la maison et partagez ce que vous avez préparé. Aucune recette requise — juste l''habitude.'),
  ('8 Glasses of Water', '8 verres d''eau', 'Buvez 8 verres chaque jour.'),
  ('Gardening Club', 'Club de jardinage', 'Suivez votre jardin ensemble — arroser, planter, désherber et récolter. Parfait pour les amis des plantes qui veulent rester responsables de leur terrain.'),
  ('Book a Month', 'Un livre par mois', 'Lisez pendant 30 minutes chaque jour.'),
  ('Couch to 5K', 'Du canapé au 5K', 'Construisez une habitude de course en vous présentant tous les jours.'),
  ('No Alcohol for 30 Days', 'Pas d''alcool pendant 30 jours', 'Évitez l''alcool pendant 30 jours.'),
  ('Daily Discovery', 'Découverte quotidienne', 'Apprenez quelque chose de nouveau chaque jour — un fait, une compétence, un concept ou un métier. Partagez-le avec le groupe pour que vous puissiez aussi apprendre les uns des autres.'),
  ('Digital Detox Weekend', 'Détoxication numérique le week-end', 'Deux jours complètement sans écrans inutiles.'),
  ('30-Day Read to My Kids Challenge', 'Lire à mes enfants pendant 30 jours', 'Lisez à vos enfants chaque jour pendant 30 jours. Même 10 minutes avant le coucher construit un amour de la lecture pour la vie — et vous donne une raison de ralentir ensemble.'),
  ('Whole30', 'Whole30', 'Mangez exclusivement des aliments complets chaque jour.'),
  ('No Social Media for 30 Days', 'Pas de réseaux sociaux pendant 30 jours', 'Restez hors des réseaux sociaux chaque jour.'),
  ('Cold Shower Challenge', 'Défi de douche froide', 'Prenez une douche froide tous les jours.'),
  ('Language Practice', 'Pratique des langues', 'La pratique quotidienne rend courant. Tenez-vous mutuellement sur la bonne voie, que vous appreniez l''espagnol, le japonais ou autre chose.'),
  ('Alcohol-Free', 'Sans alcool', 'Que vous soyez alcooliquement curieux, en récupération ou que vous en ayez simplement fini avec l''alcool — c''est un groupe de responsabilité à long terme, pas une remise à zéro de 30 jours. Tranquille, favorable, pas de pression.'),
  ('Nature Walks', 'Promenades dans la nature', 'Sortez, respirez, enregistrez-le. Un groupe d''responsabilité douce pour les gens qui veulent faire de la nature une habitude régulière.'),
  ('Weekend Workshop', 'Atelier de fin de semaine', 'Faites quelque chose, réparez quelque chose ou construisez quelque chose chaque week-end. Les petites victoires comptent — un patch, une couche de peinture, une charnière serrée.'),
  ('Morning Workout', 'Entraînement matinal', 'Faites de l''exercice avant 9h pendant 21 jours.'),
  ('The Weekly Stretch', 'Le tronçon hebdomadaire', 'Le courage personnel comme une pratique. Une fois par semaine, faites une chose qui vous met mal à l''aise — une conversation difficile, une nouvelle situation sociale, un risque créatif — et enregistrez-la. La croissance vit en dehors de votre zone de confort.'),
  ('10K Steps for a Week', '10 000 pas pendant une semaine', 'Un sprint de mouvement court de 7 jours.'),
  ('Prayer Group', 'Groupe de prière', 'Priez ensemble de manière cohérente. Enregistrez votre temps calme quotidien et tenez-vous mutuellement ancrés.'),
  ('Read the New Testament in 260 Days', 'Lire le Nouveau Testament en 260 jours', 'Lisez un chapitre par jour.'),
  ('Track Every Dollar', 'Suivre chaque dollar', 'Enregistrez chaque dépense chaque jour.'),
  ('Running Club', 'Club de course', 'Enregistrez vos courses, partagez vos itinéraires et restez actifs. Pas de date de fin — juste des kilomètres constants.'),
  ('The Weekend Warrior', 'Le guerrier de fin de semaine', 'Une tâche de maintenance à domicile par semaine. Réparer une fuite, peindre une pièce, entretenir la voiture — quoi que ce soit ce week-end. Enregistrez-le et gardez la maison en bon état ensemble.'),
  ('Out There Doing It', 'Dehors et faire des trucs', 'Aucun projet spécifique, aucun objectif spécifique. Juste un groupe de gens qui se présentent et prouvent qu''ils ont fait quelque chose de productif chaque jour. Enregistrez-le. Tout compte.'),
  ('Writers'' Room', 'Salle des écrivains', 'Écrivez de manière cohérente avec des gens qui comprennent la lutte. Enregistrez votre décompte de mots ou simplement que vous vous êtes présenté.'),
  ('Staying Connected', 'Rester connecté', 'Les relations ont besoin d''entretien. Un groupe pour ceux qui veulent faire un contact régulier avec les amis et la famille une habitude non négociable — pas seulement un défi de 30 jours.'),
  ('10K Steps Daily', '10 000 pas quotidiens', 'Faites 10 000 pas tous les jours pendant 30 jours.'),
  ('Inbox Zero for a Month', 'Boîte de réception zéro pendant un mois', 'Videz votre boîte de réception tous les jours.'),
  ('Daily Steps', 'Pas quotidiens', '10 000 pas est le défi. Ceci est le mode de vie. Un groupe permanent pour les gens qui enregistrent leurs pas tous les jours et veulent que les autres fassent de même.'),
  ('Book Club', 'Club de lecture', 'Lisez ensemble, partagez vos pensées, tenez-vous mutuellement au rythme. Fonctionne pour tout genre ou vitesse de lecture.'),
  ('100 Pushups a Day', '100 pompes par jour', 'Poussez votre cohérence avec 100 pompes quotidiennes.'),
  ('30 Days of Kindness', '30 jours de gentillesse', 'Accomplir un acte gentil quotidien.'),
  ('Skill Building', 'Développement des compétences', 'Choisissez une compétence, pratiquez de manière cohérente, enregistrez vos séances. Que ce soit la guitare, la menuiserie, les échecs ou l''aquarelle — la pratique délibérée a besoin de responsabilité.'),
  ('30-Day No Sugar Challenge', 'Défi sans sucre pendant 30 jours', 'Évitez le sucre ajouté tous les jours pendant 30 jours.'),
  ('Bird Watching', 'Observation des oiseaux', 'Enregistrez vos observations, suivez votre liste de vie et partagez la joie des oiseaux avec des gens qui comprennent.'),
  ('Gratitude Journal', 'Journal de gratitude', 'Trois choses, chaque jour. Une pratique simple qui se compose au fil du temps. Responsabilité continue pour ceux qui sont passés du défi à l''habitude.'),
  ('Creative Practice', 'Pratique créative', 'Créez quelque chose — n''importe quoi — de manière cohérente. Peinture, photographie, musique, poterie, design. Le médium n''a pas d''importance ; se présenter le fait.'),
  ('DIY & Home Projects', 'Projets de bricolage et à domicile', 'Maintenez l''élan de vos projets à domicile. Enregistrez votre progression, partagez sur quoi vous travaillez et obtenez la responsabilité de terminer réellement.'),
  ('No Spend Challenge', 'Défi sans dépense', 'Pas d''achats inutiles chaque jour.'),
  ('Daily Coding', 'Codage quotidien', '30 jours de code vous fait prendre l''habitude. C''est là que vous le maintenez. Un groupe pour les développeurs, les apprenants et les constructeurs qui veulent écrire du code tous les jours — indéfiniment.'),
  ('Save $5 a Day', 'Économiser 5 € par jour', 'Économisez 5 € chaque jour.'),
  ('The Polymath Project', 'Le projet polymathe', 'Un groupe pour les vraiment curieux. Chaque jour, apprenez une nouvelle chose — n''importe quoi — et enregistrez un bref résumé de ce que c''était et pourquoi cela compte. L''objectif est l''ampleur, pas la profondeur.'),
  ('Sleep Accountability', 'Responsabilité du sommeil', 'Le sommeil est la fondation de tout. Enregistrez vos heures, tenez-vous mutuellement à des heures de coucher cohérentes et sentez la différence.'),
  ('50 Books in a Year', '50 livres en un an', 'Maintenez la lecture quotidienne et la progression hebdomadaire des livres.'),
  ('Daily Hydration', 'Hydratation quotidienne', 'Huit verres semble simple. Le faire tous les jours sans responsabilité est plus difficile qu''il n''y paraît. Un groupe décontracté pour vous tenir mutuellement en buvant suffisamment d''eau.'),
  ('Read the Bible in 400 days', 'Lire la Bible en 400 jours', 'Lisez trois chapitres par jour'),
  ('Healthy Eating', 'Alimentation saine', 'Pas un régime — une habitude à long terme. Enregistrez quand vous avez bien mangé, partagez ce qui fonctionne et tenez-vous mutuellement sur la bonne voie sans la pression tout ou rien.'),
  ('7 Hours of Sleep', '7 heures de sommeil', 'Atteindre au moins 7 heures chaque nuit.'),
  ('Plank Challenge', 'Défi de planche', 'Planches quotidiennes avec progression hebdomadaire.'),
  ('Family Fitness', 'Remise en forme familiale', 'Faites bouger toute la famille. Suivez les pas, les entraînements ou tout ce que vous faites activement ensemble.'),
  ('Strength Training', 'Entraînement en force', 'Devenez fort ensemble. Enregistrez vos séances, suivez vos levées et tenez-vous mutuellement cohérents.'),
  ('Gym Buddies', 'Copains de gym', 'Présentez-vous, enregistrez-le, tenez-vous mutuellement responsables. Le groupe qui s''entraîne ensemble, reste ensemble.'),
  ('Phone Call a Day', 'Un appel téléphonique par jour', 'Passez un appel téléphonique chaque jour.'),
  ('Language Learning Sprint', 'Sprint d''apprentissage des langues', 'Étudiez la langue pendant 15 minutes quotidiennement.'),
  ('Cook at Home', 'Cuisiner à la maison', 'Cuisinez à la maison chaque jour.'),
  ('Savings Group', 'Groupe d''épargne', 'Économisez de manière cohérente et tenez-vous mutuellement. Enregistrez vos contributions et regardez l''habitude s''accrocher.'),
  ('Daily Journaling', 'Journalisation quotidienne', 'Écrivez dans votre journal quotidiennement.'),
  ('Daily Scripture', 'Écriture sainte quotidienne', 'Bible, Coran, Torah ou tout texte sacré — une maison permanente pour ceux qui veulent lire les écritures de manière cohérente et se tenir mutuellement responsables de la pratique.'),
  ('Reach Out Daily', 'Tendre la main quotidiennement', 'Contactez une personne chaque jour.'),
  ('Budget Accountability', 'Responsabilité budgétaire', 'Suivez chaque dollar ensemble. Enregistrez quand vous avez révisé votre budget ou suivi vos dépenses pour la journée. La sensibilisation est la première étape.')
) AS v(en_title, fr_title, fr_description)
JOIN group_templates t ON t.title = v.en_title
ON CONFLICT (template_id, language) DO NOTHING;

